package com.example.vezbe3;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.recyclerview.widget.RecyclerView;

import com.bumptech.glide.Glide;

import java.util.ArrayList;

public class KontaktAdapter extends RecyclerView.Adapter<KontaktAdapter.ViewHolder> {

    private ArrayList<Kontakt> kontakti = new ArrayList<Kontakt>();

    /**
     * Provide a reference to the type of views that you are using
     * (custom ViewHolder)
     */
    public static class ViewHolder extends RecyclerView.ViewHolder {
        private final TextView ime;
        private final TextView prezime;
        private final TextView telefon;
        private final TextView skype;
        public ViewHolder(View view) {
            super(view);
            // Define click listener for the ViewHolder's View

            ime = (TextView) view.findViewById(R.id.twIme);
            prezime = (TextView) view.findViewById(R.id.twPrezime);
            telefon = (TextView) view.findViewById(R.id.twTelefon);
            skype = (TextView) view.findViewById(R.id.twSkype);
        }
        private void bind(Kontakt kontakt) {
            ime.setText(kontakt.getIme());
            prezime.setText(kontakt.getPrezime());
            telefon.setText(kontakt.getTelefon());
            skype.setText(kontakt.getSkype());
            ImageView image = itemView.findViewById(R.id.imageView);
            Glide.with(itemView)
                    .load("data:image/jpeg;base64,")
                    .into(image);
        }
        public TextView getIme() {
            return ime;
        }

        public TextView getPrezime() {
            return prezime;
        }

        public TextView getTelefon() {
            return telefon;
        }

        public TextView getSkype() {
            return skype;
        }
    }


    public KontaktAdapter(ArrayList<Kontakt> kontakti) {
        this.kontakti = kontakti;
    }

    // Create new views (invoked by the layout manager)
    @Override
    public ViewHolder onCreateViewHolder(ViewGroup viewGroup, int viewType) {
        // Create a new view, which defines the UI of the list item
        View view = LayoutInflater.from(viewGroup.getContext())
                .inflate(R.layout.jedanred, viewGroup, false);

        return new ViewHolder(view);
    }

    // Replace the contents of a view (invoked by the layout manager)
    @Override
    public void onBindViewHolder(ViewHolder viewHolder, final int position) {
        Kontakt trenutniKontakt = kontakti.get(position);
        viewHolder.bind(trenutniKontakt);
    }

    // Return the size of your dataset (invoked by the layout manager)
    @Override
    public int getItemCount() {
        return kontakti.size();
    }
}
